const Deku = require('./src/api')
const deku = new Deku()
const fs = require('fs')
async function avatarv2() {
  const id = 3,
    bgtext = "Joshua",
    signature = "Joshua Sy",
    color = "black";
  const img = await deku.profilev2(id, bgtext, signature, color);
  const p = "profile2.png";
  fs.writeFileSync(p, img)
  console.log("ok")
}
